import { Component } from '@angular/core';
import { Participant } from '../participant.model';

@Component({
  selector: 'app-register-participant',
  templateUrl: './register-participant.component.html'
})
export class RegisterParticipantComponent {
  participant: Participant = new Participant();

  registerParticipant() {
    if (this.isFormValid()) {
      const participants = JSON.parse(localStorage.getItem('participants') || '[]');
      participants.push(this.participant);
      localStorage.setItem('participants', JSON.stringify(participants));
      alert('Registro aceptado');
    } else {
      alert('Por favor, complete todos los campos del formulario');
    }
  }

  private isFormValid(): boolean {
    return this.participant.name !== undefined &&
      this.participant.address !== undefined &&
      this.participant.dateOfBirth !== undefined &&
      this.participant.email !== undefined &&
      this.participant.phoneNumber !== undefined &&
      this.participant.organization !== undefined &&
      this.participant.profession !== undefined &&
      this.participant.position !== undefined;
  }
}
<form (ngSubmit)="registerParticipant()">
  <div>
    <label for="name">Nombre:</label>
    <input type="text" id="name" [(ngModel)]="participant.name" name="name" required>
  </div>
  <div>
    <label for="address">Dirección:</label>
    <input type="text" id="address" [(ngModel)]="participant.address" name="address" required>
  </div>
  <div>
    <label for="dateOfBirth">Fecha de Nacimiento:</label>
    <input type="date" id="dateOfBirth" [(ngModel)]="participant.dateOfBirth" name="dateOfBirth" required>
  </div>
  <div>
    <label for="email">Email:</label>
    <input type="email" id="email" [(ngModel)]="participant.email" name="email" required>
  </div>
  <div>
    <label for="phoneNumber">Teléfono:</label>
    <input type="text" id="phoneNumber" [(ngModel)]="participant.phoneNumber" name="phoneNumber" required>
  </div>
  <div>
    <label for="organization">Organización:</label>
    <input type="text" id="organization" [(ngModel)]="participant.organization" name="organization" required>
  </div>
  <div>
    <label for="profession">Profesión:</label>
    <input type="text" id="profession" [(ngModel)]="participant.profession" name="profession" required>
  </div>
  <div>
    <label for="position">Cargo:</label>
    <input type="text" id="position" [(ngModel)]="participant.position" name="position" required>
  </div>
  <button type="submit">Registrar</button>
</form>
