import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AccountService } from '../services/account.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  user: any = {};

  constructor(private accountService: AccountService, private router: Router) { }

  login() {
    this.accountService.login(this.user).subscribe(
      response => {
        localStorage.setItem('user', JSON.stringify(response));
        this.router.navigate(['/events']);
      },
      error => {
        console.log('Login failed', error);
      }
    );
  }

  navigateToRegister() {
    this.router.navigate(['/register']);
  }
}
<h2>Logearse</h2>
<form (ngSubmit)="login()">
  <div>
    <label for="email">Correo:</label>
    <input type="email" id="email" [(ngModel)]="user.email" name="email" required>
  </div>
  <div>
    <label for="password">Contraseña:</label>
    <input type="password" id="password" [(ngModel)]="user.password" name="password" required>
  </div>
  <button type="submit">Login</button>
  <button type="button" (click)="navigateToRegister()">Registrarse</button>
</form>
